function contactOnChange(sele) {
      if (sele.value=="homePhone"){
           liContact = document.getElementById("homePhone");
           liContact.style.display = "";

      }else if(sele.value=="mobile1") {
           liContact = document.getElementById("mobile1");
           liContact.style.display = "";
      }else if(sele.value=="mobile2") {
           liContact = document.getElementById("mobile2");
           liContact.style.display = "";
      }else if(sele.value=="office") {
           liContact = document.getElementById("office");
           liContact.style.display = "";
      }else if(sele.value=="fax"){
          liContact = document.getElementById("fax");
           liContact.style.display = "";
      }else{
          liContact = document.getElementById("other");
           liContact.style.display = "";
      }

}

